import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
        imageUrl: 'https://picsum.photos/200',
        tags: []
    };

    styles = {
      fontSize: 30,
        fontWeight: "bold"
    };

    renderTags() {
        if (this.state.tags.length === 0) return <p>Não existem tags! Haha!</p>;

        return <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>;
    }
    render() {
       return (
            <div>
                <img src={this.state.imageUrl} alt=""/>
                <span style={{fontSize: 30 }} className={this.getBadgeClasses()}>{this.formatCount()}</span>

            <h1 style={this.styles} >Olá dumbo! </h1>
            <button  className={this.styles}>Incremento</button>
                <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>
                {this.renderTags()}
            </div>

    );
    }

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0 ) ? "warning" : "primary"
        return classes;
    }

    formatCount() {
        const { count } = this.state;
        return count === 0 ? "Zero" : count;
    }
}

export default Counter;
